mapper.select:
SELECT

FROM user_info
WHERE 1 = 1

    AND
    id = #{record.id}


    AND
    username = #{record.username}


    AND
    password = #{record.password}


    AND
    email = #{record.email}


    AND
    role = #{record.role}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}




    LIMIT #{start}, #{rows}

SELECT
count(*)
FROM user_info
WHERE 1 = 1

    AND
    id = #{record.id}


    AND
    username = #{record.username}


    AND
    password = #{record.password}


    AND
    email = #{record.email}


    AND
    role = #{record.role}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}




    LIMIT #{start}, #{rows}

mapper.delete:
delete from user_info
where id in

#{item}

mapper.select:
select
from user_info where id in
#{item}
