mapper.select:
SELECT

FROM shopping_cart
WHERE 1 = 1

    AND
    id = #{record.id}


    AND
    book_id = #{record.bookId}


    AND
    user_id = #{record.userId}


    AND
    status = #{record.status}


    AND
    number = #{record.number}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}



    LIMIT #{start}, #{rows}

SELECT
count(*)
FROM shopping_cart
WHERE 1 = 1

    AND
    id = #{record.id}


    AND
    book_id = #{record.bookId}


    AND
    user_id = #{record.userId}


    AND
    status = #{record.status}


    AND
    number = #{record.number}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}



    LIMIT #{start}, #{rows}

mapper.delete:
delete from shopping_cart
where id in

    #{item}

mapper.select:
select
from shopping_cart where id in
#{item}
